mapper.select:
SELECT ee.id AS eeId, ee.real_name AS realName, ee.ee_no AS eeNo, ee.sex AS sex, ee.phone AS phone, ee.email AS email, group_concat(gr.grade_name) AS gradeNames, eg.sort_no AS sortNo FROM examinee ee LEFT JOIN ee_grade eg ON ee.id = eg.ee_id AND eg.status = '1' LEFT JOIN grade gr ON eg.grade_id = gr.id AND gr.status != '4' AND gr.er_id = #{erId}
ee.id IN ( SELECT examinee_id FROM er_ee WHERE examiner_id = #{erId} AND cur_status = '3' )
AND ( ee.real_name LIKE CONCAT('%',#{searchString},'%') OR ee.ee_no LIKE CONCAT('%',#{searchString},'%') OR ee.phone LIKE CONCAT('%',#{searchString},'%') )
AND gr.id = #{searchClass}
GROUP BY ee.id ORDER BY gr.id ASC ,eg.sort_no ASC

mapper.delete:
DELETE eg FROM ee_grade eg
WHERE eg.ee_id = #{eeId}
AND eg.grade_id IN (
    SELECT id FROM grade where er_id = #{erId}
)
