mapper.select:
SELECT p.id AS id, p.papers_name AS papersName, p.exam_time AS examTime, p.full_score AS fullScore, p.end_time AS endTime, p.status AS status, (SELECT count(*) FROM score s WHERE p.id = s.paper_id) AS examNum FROM papers p
p.status != '4' and p.examiner_id = #{teachersId}
and p.papers_name LIKE CONCAT('%',#{papersName},'%')
