mapper.select:
SELECT s.id AS scoreId, s.score AS score, s.blur_start_time AS blurStartTime, s.commit_time AS commitTime, ee.real_name AS realName, ee.ee_no AS eeNo, p.full_score AS fullScore FROM score s LEFT JOIN examinee ee ON s.ee_id = ee.id LEFT JOIN papers p ON s.paper_id = p.id WHERE s.paper_id = #{paperId}
AND ( ee.real_name LIKE CONCAT('%',#{searchEeInfo},'%') OR ee.ee_no LIKE CONCAT('%',#{searchEeInfo},'%') )
ORDER BY s.score DESC
